import React, { useState, useEffect } from "react";

function App() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [audience, setAudience] = useState("");
  const [tone, setTone] = useState("Funny");
  const [count, setCount] = useState(3);
  const [taglines, setTaglines] = useState([]);
  const [history, setHistory] = useState([]);

  const API_BASE = "http://127.0.0.1:5000"; // Flask backend

  // Fetch history on page load
  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const res = await fetch(`${API_BASE}/history`);
      const data = await res.json();
      setHistory(data.history || []);
    } catch (err) {
      console.error("Error fetching history:", err);
    }
  };

  const handleGenerate = async () => {
    const payload = { name, description, audience, tone, count };

    try {
      const res = await fetch(`${API_BASE}/tagline`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (!res.ok) throw new Error("Failed to generate taglines");

      const data = await res.json();
      setTaglines(data.taglines || []);
      fetchHistory(); // refresh history after saving
    } catch (err) {
      console.error("Error generating:", err);
      alert("❌ Failed to connect to backend. Is Flask running?");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "700px", margin: "auto" }}>
      <h1>AI Tagline Generator</h1>

      <div style={{ marginBottom: "15px" }}>
        <label>Product Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ width: "100%", marginBottom: "10px" }}
        />

        <label>Product Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          rows={3}
          style={{ width: "100%", marginBottom: "10px" }}
        />

        <label>Target Audience:</label>
        <input
          type="text"
          value={audience}
          onChange={(e) => setAudience(e.target.value)}
          style={{ width: "100%", marginBottom: "10px" }}
        />

        <label>Tone:</label>
        <select
          value={tone}
          onChange={(e) => setTone(e.target.value)}
          style={{ width: "100%", marginBottom: "10px" }}
        >
          <option>Funny</option>
          <option>Professional</option>
          <option>Luxury</option>
          <option>Casual</option>
        </select>

        <label>Number of Taglines:</label>
        <input
          type="number"
          value={count}
          onChange={(e) => setCount(Number(e.target.value))}
          min="1"
          max="10"
          style={{ width: "100%", marginBottom: "10px" }}
        />

        <button onClick={handleGenerate} style={{ padding: "10px 20px" }}>
          Generate
        </button>
      </div>

      {taglines.length > 0 && (
        <div>
          <h2>Generated Taglines</h2>
          <ul>
            {taglines.map((t, i) => (
              <li key={i}>{t}</li>
            ))}
          </ul>
        </div>
      )}

      <div style={{ marginTop: "20px" }}>
        <h2>History</h2>
        {history.length === 0 ? (
          <p>No taglines generated yet.</p>
        ) : (
          <ul>
            {history.map((item, i) => (
              <li key={i}>
                <b>{item.name}</b>: {item.tagline}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
