import React, { useState } from 'react';

function TaglineForm({ onGenerate }) {
  const [form, setForm] = useState({
    name: '',
    description: '',
    audience: '',
    tone: 'Funny',
    count: 3
  });

  const [taglines, setTaglines] = useState([]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch('http://localhost:5000/tagline', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });
    const data = await res.json();
    setTaglines(data.taglines);
    onGenerate();
  };

  return (
    <div>
      <h2>Generate Taglines</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Product Name" onChange={handleChange} /><br/>
        <textarea name="description" placeholder="Description" onChange={handleChange}></textarea><br/>
        <input name="audience" placeholder="Target Audience" onChange={handleChange} /><br/>
        <select name="tone" onChange={handleChange}>
          <option>Funny</option>
          <option>Professional</option>
          <option>Luxury</option>
          <option>Casual</option>
        </select><br/>
        <input type="number" name="count" min="1" max="10" value={form.count} onChange={handleChange} /><br/>
        <button type="submit">Generate</button>
      </form>
      <ul>
        {taglines.map((t, i) => <li key={i}>{t}</li>)}
      </ul>
    </div>
  );
}

export default TaglineForm;
