import React, { useState } from "react";

function TaglineForm({ onGenerate, suggestions = [] }) {
  const [product, setProduct] = useState("");
  const [features, setFeatures] = useState("");
  const [audience, setAudience] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onGenerate({ product, features, audience });
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-white shadow rounded">
      <div className="mb-2">
        <label className="block">Product Name</label>
        <input
          type="text"
          value={product}
          onChange={(e) => setProduct(e.target.value)}
          className="border rounded p-2 w-full"
          required
        />
      </div>

      <div className="mb-2">
        <label className="block">Key Features</label>
        <input
          type="text"
          value={features}
          onChange={(e) => setFeatures(e.target.value)}
          className="border rounded p-2 w-full"
          required
        />
      </div>

      <div className="mb-2">
        <label className="block">Target Audience</label>
        <input
          type="text"
          value={audience}
          onChange={(e) => setAudience(e.target.value)}
          className="border rounded p-2 w-full"
          required
        />
      </div>

      <button
        type="submit"
        className="bg-blue-600 text-white p-2 rounded mt-2"
      >
        Generate Tagline
      </button>

      {suggestions.length > 0 && (
        <div className="mt-4">
          <h3 className="font-bold">Generated Suggestions:</h3>
          <ul className="list-disc pl-6">
            {suggestions.map((s, i) => (
              <li key={i}>{s}</li>
            ))}
          </ul>
        </div>
      )}
    </form>
  );
}

export default TaglineForm;
