import React, { useState } from "react";
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  Card,
  CardContent,
  CircularProgress,
} from "@mui/material";
import RocketLaunchIcon from "@mui/icons-material/RocketLaunch";

function App() {
  const [product, setProduct] = useState("");
  const [features, setFeatures] = useState("");
  const [audience, setAudience] = useState("");
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await fetch("http://localhost:5000/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ product, features, audience }),
      });

      const data = await response.json();
      if (response.ok) {
        setResult(data);
      } else {
        setError(data.error || "Something went wrong");
      }
    } catch (err) {
      setError("Failed to connect to backend");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 6 }}>
      <Typography variant="h4" align="center" gutterBottom fontWeight="bold">
        🚀 AI Tagline Generator
      </Typography>
      <Typography variant="subtitle1" align="center" color="text.secondary" gutterBottom>
        Generate catchy taglines for your product instantly
      </Typography>

      <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
        <TextField
          fullWidth
          label="Product"
          value={product}
          onChange={(e) => setProduct(e.target.value)}
          margin="normal"
          required
        />
        <TextField
          fullWidth
          label="Key Features"
          value={features}
          onChange={(e) => setFeatures(e.target.value)}
          margin="normal"
          required
        />
        <TextField
          fullWidth
          label="Target Audience"
          value={audience}
          onChange={(e) => setAudience(e.target.value)}
          margin="normal"
          required
        />

        <Button
          type="submit"
          variant="contained"
          fullWidth
          sx={{ mt: 2, py: 1.5, fontSize: "16px", borderRadius: "12px" }}
          startIcon={<RocketLaunchIcon />}
          disabled={loading}
        >
          {loading ? <CircularProgress size={24} color="inherit" /> : "Generate Tagline"}
        </Button>
      </Box>

      {error && (
        <Typography color="error" align="center" sx={{ mt: 3 }}>
          ❌ {error}
        </Typography>
      )}

      {result && (
        <Card sx={{ mt: 4, p: 2, borderRadius: "16px", boxShadow: 4 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              ✨ Main Tagline
            </Typography>
            <Typography variant="body1" sx={{ mb: 2, fontWeight: "bold" }}>
              {result.tagline}
            </Typography>

            <Typography variant="h6" gutterBottom>
              🎯 Alternatives
            </Typography>
            {result.alternatives.map((alt, i) => (
              <Typography key={i} variant="body2" sx={{ mb: 1 }}>
                • {alt}
              </Typography>
            ))}
          </CardContent>
        </Card>
      )}
    </Container>
  );
}

export default App;
