import React, { useState } from "react";

function TaglineForm({ onGenerate, suggestions = [] }) {
  const [product, setProduct] = useState("");
  const [features, setFeatures] = useState("");
  const [audience, setAudience] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onGenerate({ product, features, audience });
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="w-full max-w-md bg-white shadow-lg rounded-xl p-6"
    >
      <div className="mb-4">
        <label className="block font-semibold mb-1">ðŸ“¦ Product Name</label>
        <input
          type="text"
          value={product}
          onChange={(e) => setProduct(e.target.value)}
          className="border rounded-lg p-2 w-full focus:ring-2 focus:ring-blue-500"
          placeholder="Enter your product name"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block font-semibold mb-1">âœ¨ Key Features</label>
        <input
          type="text"
          value={features}
          onChange={(e) => setFeatures(e.target.value)}
          className="border rounded-lg p-2 w-full focus:ring-2 focus:ring-blue-500"
          placeholder="e.g. eco-friendly, fast, smart"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block font-semibold mb-1">ðŸŽ¯ Target Audience</label>
        <input
          type="text"
          value={audience}
          onChange={(e) => setAudience(e.target.value)}
          className="border rounded-lg p-2 w-full focus:ring-2 focus:ring-blue-500"
          placeholder="e.g. students, professionals"
          required
        />
      </div>

      <button
        type="submit"
        className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg w-full transition duration-300"
      >
        ðŸš€ Generate Tagline
      </button>

      {suggestions.length > 0 && (
        <div className="mt-6">
          <h3 className="font-bold text-lg mb-2 text-green-700">
            âœ… Generated Taglines:
          </h3>
          <ul className="list-disc pl-6 space-y-1 text-gray-700">
            {suggestions.map((s, i) => (
              <li key={i} className="bg-gray-100 rounded p-2">
                {s}
              </li>
            ))}
          </ul>
        </div>
      )}
    </form>
  );
}

export default TaglineForm;
